createChar(1, char_cel); lcd.init(); // инициализация LCD lcd.backlight(); // включаем подсветку // Вывод приветствия lcd.setCursor(0, 0); lcd.print(\\"Hello DRON\\"); lcd.setCursor(0, 1); lcd.print(\\" Ver 2.0.2 \\"); // задержка delay (5000); lcd.clear(); lcd.print(\\"Solder:\\"); lcd.setCursor(0, 1); lcd.print(\\"Fen:\\"); } void loop() { //основной цикл /-------------- чтение и Преобразовывание значении -------------------------------/ setSolderTemp = map(analogRead(pinSolderIn), 0, 1023, 0, 450); solderTCouple = map(analogRead(pinSolderTCouple), 0, 1023, 0, 500); setHotAirTemp = map(analogRead(pinHotAirIn), 0, 1023, 0, 460); hotAirTCouple = map(analogRead(pinHotAirTCouple), 0, 1023, 0, 500); setHotAirCooler = map(analogRead(pinHotAirCoolerIn), 0, 1023, 80, 255); displayHotAirCooler = map(analogRead(pinHotAirCoolerIn), 0, 1023, 0, 99); //SleepState = digitalRead(pinSleepState); SleepState = 0 ; SolderButton = digitalRead(pinSolderButton); HotAirButton = digitalRead(pinHotAirButton); Gerkon = digitalRead(pinGerkon); TT = millis(); /---------------------------------------------------------------------------------/ /---------------------------------работа паяльника--------------------------------/ solder(); /---------------------------------------------------------------------------------/ /---------------------------------работа фена-------------------------------------/ fen(); /--------------------------------------------------------------------------------/ /--------------------усреднение значений измеренных температур-------------------/ if (outN == 0) { sTCouple = 0; hATCouple = 0; } if (outN < 50) { ++outN; STC = STC + solderTCouple; HAT = HAT + hotAirTCouple; } else { sTCouple = STC / 50; hATCouple = HAT / 50; STC = 0; HAT = 0; outN = 0; out();//вывод на экран } /---------------------------------------------------------------------------/ } void solder() {//подпрограмма паяльника /---------------Защита от обрыва/замыкания термопары паяльника--------------/ i